package loops;

public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		char ch= 'A';
		for(i=1;i<=5;i++)
		{
			for(j=2*(5-i);j>=0;j--)
			{
				
					System.out.print(ch+"\t");
		
					System.out.print("  "+"\t");
			}
				
				}
				System.out.println();


	}

	}


